        "pathFromRoot": "examples/03-ui-components/02-formatting-toolbar-buttons",
        "files": {
          "App.tsx": "import \"@blocknote/core/fonts/inter.css\";\nimport { BlockNoteView } from \"@blocknote/mantine\";\nimport \"@blocknote/mantine/style.css\";\nimport {\n  BasicTextStyleButton,\n  BlockTypeSelect,\n  ColorStyleButton,\n  CreateLinkButton,\n  FileCaptionButton,\n  FileReplaceButton,\n  FormattingToolbar,\n  FormattingToolbarController,\n  NestBlockButton,\n  TextAlignButton,\n  UnnestBlockButton,\n  useCreateBlockNote,\n} from \"@blocknote/react\";\n\nimport { BlueButton } from \"./BlueButton\";\n\nexport default function App() {\n  // Creates a new editor instance.\n  const editor = useCreateBlockNote({\n    initialContent: [\n      {\n        type: \"paragraph\",\n        content: \"Welcome to this demo!\",\n      },\n      {\n        type: \"paragraph\",\n        content: [\n          {\n            type: \"text\",\n            text: \"You can now toggle \",\n            styles: {},\n          },\n          {\n            type: \"text\",\n            text: \"blue\",\n            styles: { textColor: \"blue\", backgroundColor: \"blue\" },\n          },\n          {\n            type: \"text\",\n            text: \" and \",\n            styles: {},\n          },\n          {\n            type: \"text\",\n            text: \"code\",\n            styles: { code: true },\n          },\n          {\n            type: \"text\",\n            text: \" styles with new buttons in the Formatting Toolbar\",\n            styles: {},\n          },\n        ],\n      },\n      {\n        type: \"paragraph\",\n        content: \"Select some text to try them out\",\n      },\n      {\n        type: \"image\",\n        props: {\n          url: \"https://interactive-examples.mdn.mozilla.net/media/cc0-images/grapefruit-slice-332-332.jpg\",\n        },\n      },\n      {\n        type: \"paragraph\",\n        content:\n          \"Notice that the buttons don't appear when the image block above is selected, as it has no inline content.\",\n      },\n      {\n        type: \"paragraph\",\n      },\n    ],\n  });\n\n  // Renders the editor instance.\n  return (\n    <BlockNoteView editor={editor} formattingToolbar={false}>\n      <FormattingToolbarController\n        formattingToolbar={() => (\n          <FormattingToolbar>\n            <BlockTypeSelect key={\"blockTypeSelect\"} />\n\n            {/* Extra button to toggle blue text & background */}\n            <BlueButton key={\"customButton\"} />\n\n            <FileCaptionButton key={\"fileCaptionButton\"} />\n            <FileReplaceButton key={\"replaceFileButton\"} />\n\n            <BasicTextStyleButton\n              basicTextStyle={\"bold\"}\n              key={\"boldStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"italic\"}\n              key={\"italicStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"underline\"}\n              key={\"underlineStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"strike\"}\n              key={\"strikeStyleButton\"}\n            />\n            {/* Extra button to toggle code styles */}\n            <BasicTextStyleButton\n              key={\"codeStyleButton\"}\n              basicTextStyle={\"code\"}\n            />\n\n            <TextAlignButton\n              textAlignment={\"left\"}\n              key={\"textAlignLeftButton\"}\n            />\n            <TextAlignButton\n              textAlignment={\"center\"}\n              key={\"textAlignCenterButton\"}\n            />\n            <TextAlignButton\n              textAlignment={\"right\"}\n              key={\"textAlignRightButton\"}\n            />\n\n            <ColorStyleButton key={\"colorStyleButton\"} />\n\n            <NestBlockButton key={\"nestBlockButton\"} />\n            <UnnestBlockButton key={\"unnestBlockButton\"} />\n\n            <CreateLinkButton key={\"createLinkButton\"} />\n          </FormattingToolbar>\n        )}\n      />\n    </BlockNoteView>\n  );\n}\n",
          "BlueButton.tsx": "import \"@blocknote/mantine/style.css\";\nimport {\n  useBlockNoteEditor,\n  useComponentsContext,\n  useEditorState,\n  useSelectedBlocks,\n} from \"@blocknote/react\";\n\n// Custom Formatting Toolbar Button to toggle blue text & background color.\nexport function BlueButton() {\n  const editor = useBlockNoteEditor();\n\n  const Components = useComponentsContext()!;\n\n  // Tracks whether the text & background are both blue.\n  const isSelected = useEditorState({\n    editor,\n    selector: ({ editor }) =>\n      editor.getActiveStyles().textColor === \"blue\" &&\n      editor.getActiveStyles().backgroundColor === \"blue\",\n  });\n\n  // Doesn't render unless a at least one block with inline content is\n  // selected. You can use a similar pattern of returning `null` to\n  // conditionally render buttons based on the editor state.\n  const blocks = useSelectedBlocks();\n  if (blocks.filter((block) => block.content !== undefined).length === 0) {\n    return null;\n  }\n\n  return (\n    <Components.FormattingToolbar.Button\n      mainTooltip={\"Blue Text & Background\"}\n      onClick={() => {\n        editor.toggleStyles({\n          textColor: \"blue\",\n          backgroundColor: \"blue\",\n        });\n      }}\n      isSelected={isSelected}\n    >\n      Blue\n    </Components.FormattingToolbar.Button>\n  );\n}\n"
        }
