          "AlertButton.tsx": "import { LinkToolbarProps, useComponentsContext } from \"@blocknote/react\";\n\n// Custom Link Toolbar button to open a browser alert.\nexport function AlertButton(props: LinkToolbarProps) {\n  const Components = useComponentsContext()!;\n\n  return (\n    <Components.LinkToolbar.Button\n      mainTooltip={\"Open Alert with URL\"}\n      onClick={() => window.alert(`Link URL: ${props.url}`)}\n    >\n      Open Alert\n    </Components.LinkToolbar.Button>\n  );\n}\n",
          "App.tsx": "import \"@blocknote/core/fonts/inter.css\";\nimport { BlockNoteView } from \"@blocknote/mantine\";\nimport \"@blocknote/mantine/style.css\";\nimport {\n  DeleteLinkButton,\n  EditLinkButton,\n  LinkToolbar,\n  LinkToolbarController,\n  OpenLinkButton,\n  useCreateBlockNote,\n} from \"@blocknote/react\";\n\nimport { AlertButton } from \"./AlertButton\";\n\nexport default function App() {\n  // Creates a new editor instance.\n  const editor = useCreateBlockNote({\n    initialContent: [\n      {\n        type: \"paragraph\",\n        content: \"Welcome to this demo!\",\n      },\n      {\n        type: \"paragraph\",\n        content: \"Hover the link below to see the modified Link Toolbar\",\n      },\n      {\n        type: \"paragraph\",\n        content: [\n          {\n            type: \"link\",\n            href: \"https://www.blocknotejs.org/\",\n            content: [\n              {\n                type: \"text\",\n                text: \"Home Page\",\n                styles: {},\n              },\n            ],\n          },\n        ],\n      },\n      {\n        type: \"paragraph\",\n      },\n    ],\n  });\n\n  // Renders the editor instance.\n  return (\n    <BlockNoteView editor={editor} linkToolbar={false}>\n      <LinkToolbarController\n        linkToolbar={(props) => (\n          <LinkToolbar {...props}>\n            <EditLinkButton\n              url={props.url}\n              text={props.text}\n              range={props.range}\n              setToolbarOpen={props.setToolbarOpen}\n              setToolbarPositionFrozen={props.setToolbarPositionFrozen}\n            />\n            <OpenLinkButton url={props.url} />\n            <DeleteLinkButton\n              range={props.range}\n              setToolbarOpen={props.setToolbarOpen}\n            />\n            {/* Extra button to open alert. */}\n            <AlertButton {...props} />\n          </LinkToolbar>\n        )}\n      />\n    </BlockNoteView>\n  );\n}\n"
