          "App.tsx": "import { BlockNoteSchema, defaultStyleSpecs } from \"@blocknote/core\";\nimport \"@blocknote/core/fonts/inter.css\";\nimport { BlockNoteView } from \"@blocknote/mantine\";\nimport \"@blocknote/mantine/style.css\";\nimport {\n  BasicTextStyleButton,\n  BlockTypeSelect,\n  ColorStyleButton,\n  CreateLinkButton,\n  FileCaptionButton,\n  FileReplaceButton,\n  FormattingToolbar,\n  FormattingToolbarController,\n  NestBlockButton,\n  TextAlignButton,\n  UnnestBlockButton,\n  useBlockNoteEditor,\n  useComponentsContext,\n  useCreateBlockNote,\n} from \"@blocknote/react\";\n\nimport { RiText } from \"react-icons/ri\";\n\nimport { Font } from \"./Font\";\n\n// Our schema with style specs, which contain the configs and implementations for styles\n// that we want our editor to use.\nconst schema = BlockNoteSchema.create({\n  styleSpecs: {\n    // Adds all default styles.\n    ...defaultStyleSpecs,\n    // Adds the Font style.\n    font: Font,\n  },\n});\n\n// Formatting Toolbar button to set the font style.\nconst SetFontStyleButton = () => {\n  const editor = useBlockNoteEditor<\n    typeof schema.blockSchema,\n    typeof schema.inlineContentSchema,\n    typeof schema.styleSchema\n  >();\n\n  const Components = useComponentsContext()!;\n\n  return (\n    <Components.FormattingToolbar.Button\n      label=\"Set Font\"\n      mainTooltip={\"Set Font\"}\n      icon={<RiText />}\n      onClick={() => {\n        const fontName = prompt(\"Enter a font name\") || \"Comic Sans MS\";\n\n        editor.addStyles({\n          font: fontName,\n        });\n      }}\n    />\n  );\n};\n\nexport default function App() {\n  // Creates a new editor instance.\n  const editor = useCreateBlockNote({\n    schema,\n    initialContent: [\n      {\n        type: \"paragraph\",\n        content: \"Welcome to this demo!\",\n      },\n      {\n        type: \"paragraph\",\n        content: [\n          {\n            type: \"text\",\n            text: \"Comic Sans MS\",\n            styles: {\n              font: \"Comic Sans MS\",\n            },\n          },\n          {\n            type: \"text\",\n            text: \" <- This text has a different font\",\n            styles: {},\n          },\n        ],\n      },\n      {\n        type: \"paragraph\",\n        content:\n          \"Highlight some text to open the Formatting Toolbar and change the font elsewhere\",\n      },\n      {\n        type: \"paragraph\",\n      },\n    ],\n  });\n\n  return (\n    <BlockNoteView editor={editor} formattingToolbar={false}>\n      {/* Replaces the default Formatting Toolbar. */}\n      <FormattingToolbarController\n        formattingToolbar={() => (\n          <FormattingToolbar>\n            <BlockTypeSelect key={\"blockTypeSelect\"} />\n\n            <FileCaptionButton key={\"fileCaptionButton\"} />\n            <FileReplaceButton key={\"replaceFileButton\"} />\n\n            <BasicTextStyleButton\n              basicTextStyle={\"bold\"}\n              key={\"boldStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"italic\"}\n              key={\"italicStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"underline\"}\n              key={\"underlineStyleButton\"}\n            />\n            <BasicTextStyleButton\n              basicTextStyle={\"strike\"}\n              key={\"strikeStyleButton\"}\n            />\n            {/* Adds SetFontStyleButton */}\n            <SetFontStyleButton />\n\n            <TextAlignButton\n              textAlignment={\"left\"}\n              key={\"textAlignLeftButton\"}\n            />\n            <TextAlignButton\n              textAlignment={\"center\"}\n              key={\"textAlignCenterButton\"}\n            />\n            <TextAlignButton\n              textAlignment={\"right\"}\n              key={\"textAlignRightButton\"}\n            />\n\n            <ColorStyleButton key={\"colorStyleButton\"} />\n\n            <NestBlockButton key={\"nestBlockButton\"} />\n            <UnnestBlockButton key={\"unnestBlockButton\"} />\n\n            <CreateLinkButton key={\"createLinkButton\"} />\n          </FormattingToolbar>\n        )}\n      />\n    </BlockNoteView>\n  );\n}\n",
