        "files": {
          "App.tsx": "import { BlockNoteSchema, defaultBlockSpecs } from \"@blocknote/core\";\nimport {\n  filterSuggestionItems,\n  insertOrUpdateBlockForSlashMenu,\n} from \"@blocknote/core/extensions\";\nimport { en } from \"@blocknote/core/locales\";\nimport \"@blocknote/core/fonts/inter.css\";\nimport { BlockNoteView } from \"@blocknote/mantine\";\nimport \"@blocknote/mantine/style.css\";\nimport {\n  SuggestionMenuController,\n  getDefaultReactSlashMenuItems,\n  useCreateBlockNote,\n} from \"@blocknote/react\";\n\nimport { RiFilePdfFill } from \"react-icons/ri\";\n\nimport { PDF } from \"./PDF\";\n\n// Updated English dictionary with entries for PDF blocks.\nconst dictionary = en;\nen.file_blocks.add_button_text[\"pdf\"] = \"Add PDF\";\nen.file_panel.embed.embed_button[\"pdf\"] = \"Embed PDF\";\nen.file_panel.upload.file_placeholder[\"pdf\"] = \"Upload PDF\";\n\n// Our schema with block specs, which contain the configs and implementations for blocks\n// that we want our editor to use.\nconst schema = BlockNoteSchema.create({\n  blockSpecs: {\n    // Adds all default blocks.\n    ...defaultBlockSpecs,\n    // Adds the PDF block.\n    pdf: PDF(),\n  },\n});\n\n// Slash menu item to insert a PDF block\nconst insertPDF = (editor: typeof schema.BlockNoteEditor) => ({\n  title: \"PDF\",\n  onItemClick: () => {\n    insertOrUpdateBlockForSlashMenu(editor, {\n      type: \"pdf\",\n    });\n  },\n  aliases: [\"pdf\", \"document\", \"embed\", \"file\"],\n  group: \"Other\",\n  icon: <RiFilePdfFill />,\n});\n\nexport default function App() {\n  // Creates a new editor instance.\n  const editor = useCreateBlockNote({\n    dictionary,\n    schema,\n    initialContent: [\n      {\n        type: \"paragraph\",\n        content: \"Welcome to this demo!\",\n      },\n      {\n        type: \"pdf\",\n        props: {\n          url: \"https://pdfobject.com/pdf/sample.pdf\",\n        },\n      },\n      {\n        type: \"paragraph\",\n        content: \"Press the '/' key to open the Slash Menu and add another PDF\",\n      },\n      {\n        type: \"paragraph\",\n      },\n    ],\n  });\n\n  // Renders the editor instance.\n  return (\n    <BlockNoteView editor={editor} slashMenu={false}>\n      {/* Replaces the default Slash Menu. */}\n      <SuggestionMenuController\n        triggerCharacter={\"/\"}\n        getItems={async (query) =>\n          // Gets all default slash menu items and `insertPDF` item.\n          filterSuggestionItems(\n            [...getDefaultReactSlashMenuItems(editor), insertPDF(editor)],\n            query,\n          )\n        }\n      />\n    </BlockNoteView>\n  );\n}\n",
          "PDF.tsx": "import { FileBlockConfig } from \"@blocknote/core\";\nimport {\n  createReactBlockSpec,\n  ReactCustomBlockRenderProps,\n  ResizableFileBlockWrapper,\n} from \"@blocknote/react\";\n\nimport { RiFilePdfFill } from \"react-icons/ri\";\n\nimport \"./styles.css\";\n\nexport const PDFPreview = (\n  props: Omit<\n    ReactCustomBlockRenderProps<\n      FileBlockConfig[\"type\"],\n      FileBlockConfig[\"propSchema\"],\n      FileBlockConfig[\"content\"]\n    >,\n    \"contentRef\"\n  >,\n) => {\n  return (\n    <embed\n      type={\"application/pdf\"}\n      src={props.block.props.url}\n      contentEditable={false}\n      draggable={false}\n      onClick={() => props.editor.setTextCursorPosition(props.block)}\n    />\n  );\n};\n\nexport const PDF = createReactBlockSpec(\n  {\n    type: \"pdf\",\n    propSchema: {\n      name: {\n        default: \"\" as const,\n      },\n      url: {\n        default: \"\" as const,\n      },\n      caption: {\n        default: \"\" as const,\n      },\n      showPreview: {\n        default: true,\n      },\n      previewWidth: {\n        default: undefined,\n        type: \"number\",\n      },\n    },\n    content: \"none\",\n  },\n  {\n    meta: {\n      fileBlockAccept: [\"application/pdf\"],\n    },\n    render: (props) => (\n      <ResizableFileBlockWrapper\n        {...(props as any)}\n        buttonIcon={<RiFilePdfFill size={24} />}\n      >\n        <PDFPreview {...(props as any)} />\n      </ResizableFileBlockWrapper>\n    ),\n  },\n);\n",
          "styles.css": "[data-content-type=\"pdf\"] embed {\n  height: 800px;\n  width: 100%;\n}\n"
        }
