          "App.tsx": "// See https://liveblocks.io/docs/get-started/react-blocknote to see how this\n// example was created, and an explanation for all the code.\nimport {\n  ClientSideSuspense,\n  LiveblocksProvider,\n  RoomProvider,\n} from \"@liveblocks/react/suspense\";\nimport \"@liveblocks/react-ui/styles.css\";\nimport \"@liveblocks/react-ui/styles/dark/media-query.css\";\nimport \"@liveblocks/react-tiptap/styles.css\";\n\nimport { Editor } from \"./Editor\";\nimport \"./globals.css\";\nimport \"./styles.css\";\n\nexport default function App() {\n  return (\n    <LiveblocksProvider\n      publicApiKey={\n        \"pk_prod_6iVYNrHvG98GvWioAutXrhTkpG0iQLrzUK3nfWT4_VKWl6NIrlt112YD29to9gQH\"\n      }\n    >\n      <RoomProvider id=\"my-room\">\n        <ClientSideSuspense fallback={<div>Loading</div>}>\n          <Editor />\n        </ClientSideSuspense>\n      </RoomProvider>\n    </LiveblocksProvider>\n  );\n}\n",
          "Editor.tsx": "import \"@blocknote/core/fonts/inter.css\";\nimport { BlockNoteEditor } from \"@blocknote/core\";\nimport { BlockNoteView } from \"@blocknote/mantine\";\nimport \"@blocknote/mantine/style.css\";\nimport { useCreateBlockNoteWithLiveblocks } from \"@liveblocks/react-blocknote\";\n\nimport { Threads } from \"./Threads\";\n\nexport function Editor() {\n  const editor = useCreateBlockNoteWithLiveblocks(\n    {},\n    { mentions: true },\n  ) as BlockNoteEditor;\n\n  return (\n    <div>\n      <BlockNoteView editor={editor} className=\"editor\" />\n      <Threads editor={editor} />\n    </div>\n  );\n}\n",
          "Threads.tsx": "import { BlockNoteEditor } from \"@blocknote/core\";\nimport { useThreads } from \"@liveblocks/react/suspense\";\nimport {\n  AnchoredThreads,\n  FloatingComposer,\n  FloatingThreads,\n} from \"@liveblocks/react-blocknote\";\n\nexport function Threads({ editor }: { editor: BlockNoteEditor | null }) {\n  const { threads } = useThreads({ query: { resolved: false } });\n\n  if (!editor) {\n    return null;\n  }\n\n  return (\n    <>\n      <div className=\"anchored-threads\">\n        <AnchoredThreads editor={editor} threads={threads} />\n      </div>\n      <FloatingThreads\n        editor={editor}\n        threads={threads}\n        className=\"floating-threads\"\n      />\n      <FloatingComposer editor={editor} className=\"floating-composer\" />\n    </>\n  );\n}\n",
